 'MENU' (mEdit, preload) {	mEdit, textMenuProc,	NoItems,				/* disable everything, program does the enabling */	enabled, "Edit",	 {		"Undo",			noicon, "Z", nomark, plain;		"-",			noicon, nokey, nomark, plain;		"Cut",			noicon, "X", nomark, plain;		"Copy",			noicon, "C", nomark, plain;		"Paste",			noicon, "V", nomark, plain;		"Clear",			noicon, nokey, nomark, plain	}};resource 'MENU' (mSettings, preload) {	mSettings, textMenuProc,	NoItems,				/* disable everything, program does the enabling */	enabled, "Settings",	 {		"Connection…",			noicon, "1", nomark, plain;		"File Transfer…",			noicon, "2", nomark, plain;		"Terminal…",			noicon, "3", nomark, plain	}};/* this ALRT and DITL are used as an About screen */resource 'ALRT' (rAboutAlert, purgeable) {	{40, 20, 180, 400},	rAboutAlert,	{ /* array: 4 elements */		/* [1] */		OK, visible, silent,		/* [2] */		OK, visible, silent,		/* [3] */		OK, visible, silent,		/* [4] */		OK, visible, silent	}};resource 'DITL' (rAboutAlert, purgeable) {	{	/* array DITLarray: 5 elements */		/* [1] */		{99, 285, 119, 365},		Button {			enabled,			"OK"		},		/* [2] */		{8, 8, 26, 250},		StaticText {			disabled,			"SURFER: A CommToolbox Application"		},		/* [3] */		{103, 6, 120, 251},		StaticText {			disabled,			"Copyright © 1988-90 Apple Computer."		},		/* [4] */		{52, 8, 69, 204},		StaticText {			disabled,			"Bytes by Alex, Words by Rob"		},		/* [5] */		{86, 6, 103, 211},		StaticText {			disabled,			"Based on Sample from MacDTS"		}	}};/* this ALRT and DITL are used as an error screen */resource 'ALRT' (rUserAlert, purgeable) {	{40, 20, 180, 350},	rUserAlert,	{ /* array: 4 elements */		/* [1] */		OK, visible, silent,		/* [2] */		OK, visible, silent,		/* [3] */		OK, visible, silent,		/* [4] */		OK, visible, silent	}};resource 'DITL' (rUserAlert, purgeable) {	{ /* array DITLarray: 3 elements */		/* [1] */		{100, 230, 120, 310},		Button {			enabled,			"OK"		},		/* [2] */		{10, 60, 76, 310},		StaticText {			disabled,			"^0"		},		/* [3] */		{8, 8, 40, 40},		Icon {			disabled,			2		}	}};resource 'WIND' (rWindow, preload, purgeable) {	{40, 5, 340, 512},	noGrowDocProc, visible, noGoAway, 0x0, "Surf the Wave"};/* here is the quintessential MultiFinder friendliness device, the SIZE resource */resource 'SIZE' (-1) {	dontSaveScreen,	acceptSuspendResumeEvents,	enableOptionSwitch,	canBackground,	doesActivateOnFGSwitch,	backgroundAndForeground,	dontGetFrontClicks,	ignoreAppDiedEvents,	is32BitCompatible,	isHighLevelEventAware,	localAndRemoteHLEvents,	notStationeryAware,					reserved,	reserved,	reserved,	reserved,	kPrefSize * 1024,	kMinSize * 1024		};resource 'BNDL' (rBNDL) {	'SurF',	0,	{	/* array TypeArray: 2 elements */		/* [1] */		'FREF',		{	/* array IDArray: 1 elements */			/* [1] */			0, 128		},		/* [2] */		'ICN#',		{	/* array IDArray: 1 elements */			/* [1] */			0, 128		}	}};data 'SurF' (0, "Owner resource") {	$"3B 43 6F 70 79 72 69 67 68 74 20 31 39 38 38 2D"    /* ;Copyright 1988- */	$"39 30 20 41 70 70 6C 65 20 43 6F 6D 70 75 74 65"    /* 90 Apple Compute */	$"72 20 49 6E 63 2E 20 20 41 6C 6C 20 52 69 67 68"    /* r Inc.  All Righ */	$"74 73 20 52 65 73 65 72 76 65 64 2E"                /* ts Reserved. */};resource 'FREF' (rBNDL) {	'APPL',	0,	""};resource 'ICN#' (rBNDL) {	{	/* array: 2 elements */		/* [1] */		$"04 00 00 00 20 80 00 2C 0E 02 00 50 11 07 00 00"		$"51 4A 80 00 11 0A 81 60 0E 0A 82 90 20 9A 40 00"		$"04 12 40 00 00 12 50 40 00 12 48 80 00 12 47 00"		$"00 12 47 00 00 12 47 00 FF F2 77 7F 04 12 42 11"		$"40 52 4F 80 00 12 7F C4 01 12 7F C0 48 12 4F C0"		$"00 12 47 48 00 12 47 40 00 92 47 42 24 12 4F 80"		$"00 12 4D 80 00 12 4D 90 20 92 48 82 02 12 48 80"		$"00 12 58 C0 20 0F 98 C8 00 00 00 00 04 42 22 02",		/* [2] */		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"		$"FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF FF"	}};