////	Example.c////	A simple profiler example.////	#include <Types.h>#include <OSUtils.h>#include "Profiler.h"void main(void);void test4(void);void test4(void){	int	i, j = 0;		for (i = 0; i < 500; i++)		j++;}void test3(short c);void test3(short c){	long	t;		Delay(c, &t);	test4();}int test2(int b);int test2(int b){	long	t;	Delay(b, &t);	test3(b);	test4();		return b;}void test(short a);void test(short a){	short	i;		for (i = a - 1; i >= 0; i--)		if (test2(5) != 5)			SysBeep(2);}#if __powerc#define SUFFIX "PPC"#else#define SUFFIX "68K"#endif/*  If you copy and paste the following line from the example, be sure to  make sure that you change the function table size and the stack table  size passed to ProfilerInit to appropriate values for your application.*/void main(){#if __profile__	if (!ProfilerInit(collectDetailed, bestTimeBase, 20, 5))	{#endif		test(15);#if __profile__		ProfilerDump("\pExample" SUFFIX ".prof");		ProfilerTerm();	}#endif}