// The following 2 constants need to be true if playing with PC on net#define __PC_COMPATIBLE__				0					// Be Compatible with PC#if __PC_COMPATIBLE__#define __MESSAGE_LITTLE_END__ 	1			// PC (1) Swap bytes in command messages#define __MESSAGE_EOM__					1			// PC (1) Send end of message flag after messages#define __MAC_ACK__							0			// PC (0) Do extra acquisition to make games start together#define __PC_DO__								1			// PC (1) Put do in Arbnet start where it is for PC#else#define __MESSAGE_LITTLE_END__ 	0			// MAC (0) Swap bytes in command messages#define __MESSAGE_EOM__					0			// MAC (0) Send end of message flag after messages#define __MAC_ACK__							0			// MAC (1) Do extra acquisition to make games start together#define __PC_DO__								1			// MAC (0) Put do in Arbnet start where it is for PC#endif //__PC_COMPATIBLE__#define __COMPLETE_ASYNCH__			1					// Make asynchronous calls#define __READ_HDR_COMPLETION__ 0					// Use completion routine in asynch reads of header// End of message markers#define FRAMECHAR1 				0x70				// First character of eom#define FRAMECHAR2 				0x00				// Second character of eom